def recomendar_filmes(catalogo):
    print("*-*-*- Bem-vindo à nossa locadora de filmes!! -*-*-*")
    genero = input("Digite o gênero de sua preferência: ").strip().lower()
    diretor = input("Digite o diretor de sua preferência (opcional): ").strip().lower()
    empresa_produtora = input("Digite a empresa produtora de sua preferência (opcional): ").strip().lower()
    ano = input("Digite o ano de sua preferência (opcional): ").strip()
    avaliação = input("Digite a avaliação de sua preferência (opcional): ").strip()

    recomendados = []

    for titulo, dados in catalogo.items():
        genero_correto = genero in dados['genero'].lower() if genero else True
        diretor_correto = diretor in dados['diretor'].lower() if diretor else True
        empresa_produtora_correta = empresa_produtora in dados['empresa_produtora'].lower() if empresa_produtora else True
        ano_correto = str(dados['ano']) == ano if ano else True
        avaliação_correta = str(dados['avaliação']) == avaliação if avaliação else True


        if genero_correto and ano_correto and diretor_correto and empresa_produtora_correta and avaliação_correta:
            recomendados.append((titulo, dados))

    if recomendados:
        print("\nFilmes recomendados para você:")
        for titulo, dados in recomendados:
            print(f"- {titulo} ({dados['ano']}) - Gênero: {dados['genero']}")
    else:
        print("\nDesculpe, não encontramos filmes com essas preferências.")

recomendar_filmes(catalogo)
